import React, { useState } from 'react'

const Inputs = ({value, setFunction}) => <input value={value} onChange={setFunction}></input>

const Filter = ({text, value, setFunction}) => {
  return (
    <div>{text} <Inputs value={value} setFunction={setFunction}/></div>
  )
}

const PersonForm = ({onSubmit, nameValue, numberValue, nameOnChange, numberOnChange}) => {
  return (
    <div>
      <form onSubmit={handlePersonsChange}>
        <div>name: <input value={newName} onChange={handleNameChange} /></div>
        <div>number: <input value={newNumber} onChange={handleNumberChange} /></div>
        <div>
          <button type="submit">add</button>
        </div>
      </form> 
    </div>   
  )
}
const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ])
  // Holds the current state of an input, updated with each keystroke
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState('')
  const [ newSearch, setNewSearch ] = useState('')
  const [showAll, setShowAll] = useState(true)

  // My first conditional operator!
  // const result = condition ? val1 : val2 Meaning if condition is True then val1 is used and val2 is used when False.
  const peopleToShow = showAll
  ? persons
  : persons.filter(person => person.name.toLowerCase().includes(newSearch.toLowerCase()) === true)

  // used to updated input
  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  // If search input has any characters, showAll condition becomes false because there needs to be a filter
  const handleSearchChange = (event) => {
    setNewSearch(event.target.value)
    if (event.target.value.length !== 0) {
      setShowAll(false)
    } else {
      setShowAll(true)
    }
  }

  // On form submit, stop refresh, create new object and check for duplicates
  const handlePersonsChange = (event) => {
    event.preventDefault()
    const personObject = {
      name: newName,
      number: newNumber,

    }
    checkDuplicateName(personObject)
    setNewName('')
    setNewNumber('')
  }

  // Checks current names in phonebook, alerting the user if they are trying to add a duplicate name
  const checkDuplicateName = (personInfo) => {
    for (let i in persons) {
      if (personInfo.name.toLowerCase() === persons[i].name.toLowerCase()) {
        window.alert(`${newName} is already added to phonebook`)
        return
      }
    }
    setPersons(persons.concat(personInfo))

  }

  return (
    <div>
      <h2>Phonebook</h2>
      {/* <div>search: <input text='Search:' value={newSearch} onChange={handleSearchChange}/></div> */}
      <Filter text='Search' value={newSearch} setFunction={handleSearchChange}/>
      <h2>Add New</h2>
      {/* <form onSubmit={handlePersonsChange}>
        <div>name: <input value={newName} onChange={handleNameChange} /></div>
        <div>number: <input value={newNumber} onChange={handleNumberChange} /></div>
        <div>
          <button type="submit">add</button>
        </div>
      </form> */}
      <PersonForm
        handlePersonsChange={handlePersonsChange}
        newName={newName}
        newNumber={newNumber}
        handleNameChange={handleNameChange}
        handleNumberChange={handleNumberChange}
      />
      <h2>Numbers</h2>
      <div>
        {peopleToShow.map(person => <div key={person.name}>{person.name} {person.number}</div>)}
      </div>
    </div>
  )
}

export default App
